/**
 * LOG_Build_Flagged
 * Runs when user “flag”s a receipt and we notify them.
 */

 // 1) Core context
const timestamp       = new Date().toISOString();

const workflowName    = $workflow.name;
const chatId          = $node["TG_Trigger"].json.message.chat.id;
const firstName       = $node["TG_Trigger"].json.message.from.first_name;

// 2) Which receipt got flagged
// We flagged the latest in DB; grab its ID
const flaggedRecord   = $node["DB_FlagLast"].json;  
const dbRecordId      = flaggedRecord.id ?? "N/A";

// 3) Flags & Branch info
const isUserFlag      = $node["JS_FlagDetect"].json.isFlag ? "true" : "false";
const pathTaken       = "Flagged";

// 4) Build the line
let logLine = [
  `[${timestamp}]`,

  `wkflow=${workflowName}`,
  `user=${chatId}`,
  `name=${firstName}`,
  `step=NotifyFlagged`,
  `path=${pathTaken}`,
  `userFlag=${isUserFlag}`,
  `dbId=${dbRecordId}`
].join(" ");

// 5) Escape ampersands
logLine = logLine.replace(/&/g, "^&");

// 6) Expose
items[0].json.logLine = logLine;
return items;

/**
 * LOG_Build_NoPrice
 * Runs when OCR couldn’t detect a total.
 */

// 1) Metadata
const timestamp    = new Date().toISOString();

const workflowName = $workflow.name;
const chatId       = $node["TG_Trigger"].json.message.chat.id;
const firstName    = $node["TG_Trigger"].json.message.from.first_name;

// 2) Parsed OCR values
const ocrNode      = $node["OCR_SendToAPI"].json.body || {};
const merchant     = ocrNode.merchant?.value     ?? "N/A";
const mconf        = ocrNode.merchant?.confidence ?? "N/A";
const date         = ocrNode.date?.value         ?? "N/A";
const dconf        = ocrNode.date?.confidence    ?? "N/A";
const total        = ocrNode.total?.value        ?? "N/A";
const tconf        = ocrNode.total?.confidence   ?? "N/A";

// 3) Branch info
const pathTaken    = "NoPrice";

// 4) Build a single‐line log (no inner quotes, escape &)
let logLine = [
  `[${timestamp}]`,

  `wkflow=${workflowName}`,
  `user=${chatId}`,
  `name=${firstName}`,
  `step=NotifyNoPrice`,
  `path=${pathTaken}`,
  `merchant=${merchant}`,
  `mconf=${mconf}`,
  `date=${date}`,
  `dconf=${dconf}`,
  `total=${total}`,
  `tconf=${tconf}`
].join(" ");

logLine = logLine.replace(/&/g, "^&");
items[0].json.logLine = logLine;
return items;

/**
 * LOG_Build_Final
 * (This runs after TG_ConfirmSave, on the “Price Found” branch)
 */

// 1) Core receipt data
const timestamp       = new Date().toISOString();
const chatId          = $node["TG_Trigger"].json.message.chat.id;
const firstName       = $node["TG_Trigger"].json.message.from.first_name;
const merchant        = $node["DB_InsertReceipt"].json.merchant;
const merchantConf    = $node["DB_InsertReceipt"].json.merchant_confidence;
const purchaseDate    = $node["DB_InsertReceipt"].json.purchase_date;
const dateConf        = $node["DB_InsertReceipt"].json.date_confidence;
const total           = $node["DB_InsertReceipt"].json.total_amount;
const totalConf       = $node["DB_InsertReceipt"].json.total_confidence;

// 2) Flags & branching
const hasTotal        = Boolean(total) ? "true" : "false";
const isFlaggedByUser = $node["JS_FlagDetect"].json.isFlag ? "true" : "false";
const isDuplicate     = $node["DB_InsertReceipt"].json.duplicate ?? "false";  // if you added that column

// 3) File metadata (from your download node)
const fileUniqueId    = $node["TG_DownloadFull"].json.file_unique_id;
const mimeType        = $node["TG_DownloadForSave"].binary.data.mimeType;
const fileSize        = $node["TG_DownloadForSave"].binary.data.fileSize;

// 4) OCR & HTTP details
const ocrStatusCode   = $node["OCR_SendToAPI"].json.response?.statusCode || "N/A";

// 5) Drive & DB details
const driveUrl        = $node["GD_UploadReceipt"].json.webContentLink;
const dbRecordId      = $node["DB_InsertReceipt"].json.id;

// 6) Workflow metadata
const workflowName    = $workflow.name;            // your n8n workflow name


// 7) Build the audit line (no inner quotes, escape & if needed)
let logLine = [
  `[${timestamp}]`,

  `wkflow=${workflowName}`,
  `user=${chatId}`,
  `name=${firstName}`,
  `step=ConfirmSave`,
  `merchant=${merchant}`,
  `mconf=${merchantConf}`,
  `date=${purchaseDate}`,
  `dconf=${dateConf}`,
  `total=${total}`,
  `tconf=${totalConf}`,
  `hasTotal=${hasTotal}`,
  `userFlag=${isFlaggedByUser}`,
  `dup=${isDuplicate}`,
  `fileId=${fileUniqueId}`,
  `mime=${mimeType}`,
  `size=${fileSize}`,
  `ocrCode=${ocrStatusCode}`,
  `driveUrl=${driveUrl}`,
  `dbId=${dbRecordId}`
].join(" ");

// escape any stray ampersands so that cmd.exe >> won't break
logLine = logLine.replace(/&/g, "^&");

// expose it
items[0].json.logLine = logLine;
return items;

/**
 * LOG_Build_RejectNonImage
 * Runs when a non-image (and non-“flag”) message arrives.
 */

// 1) Meta
const timestamp    = new Date().toISOString();

const workflowName = $workflow.name;
const chatId       = $node["TG_Trigger"].json.message.chat.id;
const firstName    = $node["TG_Trigger"].json.message.from.first_name;

// 2) Input details
const inputText    = $node["TG_Trigger"].json.message.text || '<no-text>';
const hasPhoto     = Array.isArray($node["TG_Trigger"].json.message.photo);
const mimeType     = hasPhoto
  ? $node["TG_Trigger"].json.message.photo[0].mime_type || 'image/unknown'
  : ($node["TG_Trigger"].json.message.document?.mime_type || 'none');

// 3) Build line (no inner quotes, escape &)
let logLine = [
  `[${timestamp}]`,

  `wkflow=${workflowName}`,
  `user=${chatId}`,
  `name=${firstName}`,
  `step=RejectNonImage`,
  `path=InvalidInput`,
  `text=${inputText}`,
  `mime=${mimeType}`
].join(" ");

logLine = logLine.replace(/&/g, '^&');
items[0].json.logLine = logLine;
return items;

/**
 * LOG_Build_Error
 * Runs after TG_NotifyError, using data from IF_Error
 */

// 1) Timestamps & run info
const timestamp    = new Date().toISOString();


// 2) Workflow info from IF_Error
const workflowName = $node["IF_Error"].json.workflow?.name    || "UnknownWF";
const execInfo     = $node["IF_Error"].json.execution || {};
const errorMsgRaw  = execInfo.error?.message                || "<no message>";
const failedNode   = execInfo.lastNodeExecuted               || "UnknownNode";

// 3) User info from original trigger (still in context)

const firstName    = $input.first().json.result.chat.first_name;

// 4) Clean up the error message
let errorMsg = errorMsgRaw
  .replace(/[\r\n]+/g, ' ')    // remove newlines
  .replace(/"/g, '')           // strip quotes
  .slice(0, 200);              // cap length

// 5) Build the log line (no inner quotes, escape &)
let logLine = [
  `[${timestamp}]`,

  `wkflow=${workflowName}`,
  `step=NotifyError`,

  `name=${firstName}`,
  `failedNode=${failedNode}`,
  `error=${errorMsg}`
].join(" ");

// 6) Escape ampersands for cmd.exe
logLine = logLine.replace(/&/g, '^&');

// 7) Expose to next node
items[0].json.logLine = logLine;
return items;

