// 1. Configure your chunk parameters
const chunkSize    = 1000;
const chunkOverlap = 200;

// 2. Grab the raw “data” field from your Document Data node
let raw = $items("Generate Doc Metadata")[0].json.output.description;


// 3. Ensure it’s a string (join arrays or serialize objects)
if (Array.isArray(raw)) {
  raw = raw.join(' ');
} else if (typeof raw !== 'string') {
  raw = JSON.stringify(raw);
}

// 4. Normalize whitespace: replace ALL newlines with a space
const text = raw.replace(/\n/g, ' ').trim();

// 5. Now do your chunk-and-overlap loop
const chunks = [];
let remainingText = text;

while (remainingText.length > 0) {
  let splitPoint = remainingText.lastIndexOf('. ', chunkSize);

  // fallback to a word boundary if no sentence break found
  if (splitPoint === -1) {
    splitPoint = remainingText.lastIndexOf(' ', chunkSize);
  }

  // if still nothing sensible, hard-cut at chunkSize
  if (splitPoint === -1 || splitPoint < chunkSize * 0.5) {
    splitPoint = chunkSize;
  }

  // extract, push, and advance (keeping overlap)
  const chunk = remainingText.substring(0, splitPoint).trim();
  chunks.push(chunk);

  remainingText = remainingText
    .substring(Math.max(0, splitPoint - chunkOverlap))
    .trim();

  // if what’s left is too small, just finish it off
  if (remainingText.length < chunkSize * 0.2) {
    if (remainingText) chunks.push(remainingText);
    break;
  }
}

// 6. Return the array of chunks
return { chunks };

